const{createApp}=Vue;createApp({data(){return{aluno:dataView}},methods:{async addAluno(){const n={nome:this.aluno.nome,email:this.aluno.email,cpf:this.aluno.cpf,alunoId:this.aluno.alunoId};try{const t=await fetch("/Aluno/Create/",{method:"POST",headers:{RequestVerificationToken:$('input[name="__RequestVerificationToken"]').val(),"Content-Type":"application/json"},body:JSON.stringify(n)});if(t.ok){const n=await t.json();n.status?window.location.href=`/Aluno/Index/${n.alunoId}`:alert("erro ao inserir aluno")}else alert("erro ao inserir aluno")}catch(t){alert("erro")}},cancelar(){window.location.href="/Home/Index"}}}).mount("#app");